from django.db import modelsfrom django.conf import settingsclass Wallet(models.Model):    """Model peněženky – uchovává zůstatek uživatele v určité měně."""    owner = models.ForeignKey(        settings.AUTH_USER_MODEL, on_delete=models.CASCADE, related_name="wallets"    )    currency = models.ForeignKey(        "market.Currency", on_delete=models.CASCADE, related_name="wallets"    )    balance = models.DecimalField(max_digits=18, decimal_places=8, default=0)    created_at = models.DateTimeField(auto_now_add=True)    class Meta:        unique_together = ("owner", "currency")    def __str__(self):        return f"{self.owner} - {self.currency.symbol} {self.balance}"class Transaction(models.Model):    """Model transakce – pohyb peněz v rámci peněženky (vklad/ výběr)."""    DEPOSIT = "DEPOSIT"    WITHDRAW = "WITHDRAW"    TX_CHOICES = [(DEPOSIT, "Deposit"), (WITHDRAW, "Withdraw")]    wallet = models.ForeignKey(Wallet, on_delete=models.CASCADE, related_name="transactions")    tx_type = models.CharField(max_length=8, choices=TX_CHOICES)    amount = models.DecimalField(max_digits=18, decimal_places=8)    created_at = models.DateTimeField(auto_now_add=True)    def __str__(self):        return f"{self.tx_type} {self.amount} {self.wallet.currency.symbol}"